import React, { useState, useRef } from 'react';

const DicomViewer = () => {
  const [folderPath, setFolderPath] = useState('');
  const [processId, setProcessId] = useState(null);
  const [viewMode, setViewMode] = useState('2d');
  const [loading, setLoading] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [resultImage, setResultImage] = useState(null);
  const [error, setError] = useState(null);

  // Create a ref for the folder input dialog
  const folderInputRef = useRef(null);

  // Base URL for API calls
  const API_URL = 'http://localhost:5001';

  const handleFolderSelection = () => {
    // Show a folder selection dialog that doesn't actually upload files
    folderInputRef.current.click();
  };

  const handleFolderChange = (e) => {
    // Modern browsers support the webkitdirectory attribute which allows folder selection
    // We just need the path, not the files themselves
    if (e.target.files && e.target.files.length > 0) {
      // In a real app, we'd handle this more robustly
      // For now, just use the value which contains the folder path
      const path = e.target.value;
      setFolderPath(path);

      // Reset previous results when new folder is selected
      setProcessId(null);
      setResultImage(null);
      setError(null);
    }
  };

  const processFolder = async () => {
    if (!folderPath) {
      setError('Please select a folder first');
      return;
    }

    setProcessing(true);
    setError(null);

    try {
      const response = await fetch(`${API_URL}/process_folder`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          folder_path: folderPath,
          view_type: viewMode
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || data.details || 'Processing failed');
      }

      if (data.success && data.images && data.images.length > 0) {
        setProcessId(data.process_id);
        // Use the full URL to the image
        setResultImage(`${API_URL}${data.images[0]}`);
      } else {
        throw new Error('No images were generated');
      }
    } catch (error) {
      console.error("Processing error:", error);
      setError(`Error processing folder: ${error.message}`);
    } finally {
      setProcessing(false);
    }
  };

  // Function to launch 3D viewer when requested
  const launch3DViewer = async () => {
    if (!folderPath) {
      setError('Please select a folder first');
      return;
    }

    try {
      const response = await fetch(`${API_URL}/launch_3d`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          folder_path: folderPath
        })
      });

      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.error || 'Failed to launch 3D viewer');
      }

      alert('3D viewer application launched. Check your desktop for the window.');
    } catch (error) {
      setError(`Error: ${error.message}`);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      <div className="bg-gray-800 text-white p-4">
        <h1 className="text-2xl font-bold">NELSVIEW: Views Dicom Images</h1>
      </div>

      <div className="flex-1 flex flex-col md:flex-row p-4 space-y-4 md:space-y-0 md:space-x-4">
        <div className="w-full md:w-1/4 bg-white rounded-lg shadow p-4">
          <h2 className="text-xl font-semibold mb-4">Controls</h2>

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Select DICOM Folder
            </label>

            {/* Hidden input for directory selection */}
            <input
              type="file"
              ref={folderInputRef}
              webkitdirectory="true"
              directory="true"
              style={{ display: 'none' }}
              onChange={handleFolderChange}
            />

            <button
              onClick={handleFolderSelection}
              className="w-full py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Select Folder
            </button>

            {folderPath && (
              <p className="text-xs text-gray-700 mt-1 truncate">
                Selected: {folderPath}
              </p>
            )}
          </div>

          {folderPath && (
            <>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  View Mode
                </label>
                <div className="flex space-x-2">
                  <button
                    onClick={() => setViewMode('2d')}
                    className={`px-4 py-2 rounded-lg ${
                      viewMode === '2d'
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-200 text-gray-800'
                    }`}
                  >
                    2D Slices
                  </button>
                  <button
                    onClick={() => setViewMode('3d')}
                    className={`px-4 py-2 rounded-lg ${
                      viewMode === '3d'
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-200 text-gray-800'
                    }`}
                  >
                    3D Hologram
                  </button>
                </div>
              </div>

              <button
                onClick={processFolder}
                disabled={processing}
                className="w-full py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-blue-300"
              >
                {processing ? 'Processing...' : 'Process DICOM Folder'}
              </button>

              {viewMode === '3d' && (
                <button
                  onClick={launch3DViewer}
                  className="mt-2 w-full py-2 px-4 bg-green-600 text-white rounded-md hover:bg-green-700"
                >
                  Launch Interactive 3D Viewer
                </button>
              )}

              {processId && (
                <div className="mt-4 p-3 bg-blue-50 rounded-lg">
                  <p className="text-sm text-blue-800">
                    <strong>Processing ID:</strong> {processId}
                  </p>
                </div>
              )}
            </>
          )}
        </div>

        <div className="w-full md:w-3/4 bg-white rounded-lg shadow p-4 flex flex-col">
          <h2 className="text-xl font-semibold mb-4">
            {viewMode === '2d' ? 'DICOM Slice Viewer' : '3D Hologram Viewer'}
          </h2>

          <div className="flex-1 relative">
            {(loading || processing) && (
              <div className="absolute inset-0 flex items-center justify-center bg-gray-200 bg-opacity-75 z-10">
                <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-blue-500"></div>
                <p className="ml-3 font-medium text-gray-700">
                  {loading ? 'Loading...' : 'Processing...'}
                </p>
              </div>
            )}

            {error && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
                {error}
              </div>
            )}

            {!resultImage && !loading && !processing && !error && (
              <div className="absolute inset-0 flex items-center justify-center bg-gray-100 border-2 border-dashed border-gray-300 rounded-lg">
                <div className="text-center">
                  <p className="text-gray-500">No DICOM files processed</p>
                  <p className="text-gray-400 text-sm mt-2">Select a folder and click "Process DICOM Folder"</p>
                </div>
              </div>
            )}

            {resultImage && (
              <img 
                src={resultImage} 
                alt="DICOM visualization" 
                className="max-w-full max-h-full object-contain mx-auto"
                style={{ minHeight: '400px' }}
              />
            )}
          </div>
        </div>
      </div>

      <div className="bg-gray-800 text-white p-2 text-center text-sm">
         NELSVIEW - Â© 2025
      </div>
    </div>
  );
};

export default DicomViewer;
