const DicomViewer = () => {
  const [folderName, setFolderName] = useState('');
  const [files, setFiles] = useState([]);
  const [uploadId, setUploadId] = useState(null);
  const [fileCount, setFileCount] = useState(0);
  const [viewMode, setViewMode] = useState('2d');
  const [loading, setLoading] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [resultImage, setResultImage] = useState(null);
  const [resultImages, setResultImages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isRotating, setIsRotating] = useState(false);
  const [error, setError] = useState(null);
  const [htmlViewer, setHtmlViewer] = useState(null);

  // Reference to the file input element
  const fileInputRef = useRef(null);

  // Base URL for API calls - make sure this matches your Flask server
  const API_URL = 'http://localhost:5001';

  // For development testing, you can use this to verify API connectivity
  const checkApiConnection = async () => {
    try {
      const response = await fetch(`${API_URL}/`, { 
        method: 'GET',
        // Adding these headers may help with CORS issues
        headers: {
          'Accept': 'application/json'
        }
      });

      if (response.ok) {
        console.log("API connection successful!");
        return true;
      } else {
        console.error("API connection failed with status:", response.status);
        return false;
      }
    } catch (error) {
      console.error("API connection error:", error);
      return false;
    }
  };

  const handleFolderSelect = (e) => {
    if (e.target.files && e.target.files.length > 0) {
      const selectedFiles = Array.from(e.target.files);

      // Filter only DICOM files
      const dicomFiles = selectedFiles.filter(file => 
        file.name.toLowerCase().endsWith('.dcm') || 
        file.name.toLowerCase().endsWith('.ima')
      );

      if (dicomFiles.length === 0) {
        setError('No DICOM files found in the selected folder');
        return;
      }

      // Get folder name from the path of the first file
      // Extract just the folder name, not the full path
      const path = e.target.files[0].webkitRelativePath;
      const folder = path.split('/')[0];

      setFolderName(folder);
      setFiles(dicomFiles);
      setFileCount(dicomFiles.length);

      // Reset previous results when new folder is selected
      setUploadId(null);
      setResultImage(null);
      setResultImages([]);
      setHtmlViewer(null);
      setError(null);

      console.log(`Selected ${dicomFiles.length} DICOM files from folder "${folder}"`);
    }
  };

  const openFolderDialog = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  const uploadFiles = async () => {
    if (files.length === 0) {
      setError('Please select a folder with DICOM files');
      return;
    }

    // Check API connection first
    const isConnected = await checkApiConnection();
    if (!isConnected) {
      setError('Cannot connect to the server. Please make sure the server is running on ' + API_URL);
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();

    // Track total size for debugging
    let totalSize = 0;

    // Add each file to formData, maintaining the folder structure in name
    files.forEach(file => {
      formData.append('files[]', file);
      totalSize += file.size;
    });

    console.log(`Total upload size: ${(totalSize / (1024 * 1024)).toFixed(2)} MB`);

    try {
      // Log upload information
      console.log(`Uploading ${files.length} DICOM files from folder "${folderName}"...`);

      const response = await fetch(`${API_URL}/upload`, {
        method: 'POST',
        body: formData,
        // Adding these headers may help with CORS issues
        headers: {
          'Accept': 'application/json'
          // Don't set Content-Type with FormData - browser will set it with boundary
        }
      });

      // Check if we got any response at all
      if (!response) {
        throw new Error('No response received from server');
      }

      // Try to parse the response as JSON
      let data;
      try {
        data = await response.json();
      } catch (jsonError) {
        console.error("Failed to parse JSON response:", jsonError);
        throw new Error('Invalid response from server');
      }

      if (!response.ok) {
        throw new Error(data.error || `Upload failed with status: ${response.status}`);
      }

      console.log(`Upload successful. Upload ID: ${data.upload_id}`);
      setUploadId(data.upload_id);
      setFileCount(data.file_count);
    } catch (error) {
      console.error("Upload error:", error);
      setError(`Error uploading files: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const processFiles = async () => {
    if (!uploadId) {
      setError('Please upload files first');
      return;
    }

    setProcessing(true);
    setError(null);

    try {
      const response = await fetch(`${API_URL}/process`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({
          upload_id: uploadId,
          view_type: viewMode
        })
      });

      // Check if we got any response
      if (!response) {
        throw new Error('No response received from server');
      }

      let data;
      try {
        data = await response.json();
      } catch (jsonError) {
        console.error("Failed to parse JSON response:", jsonError);
        throw new Error('Invalid response from server during processing');
      }

      if (!response.ok) {
        throw new Error(data.error || data.details || `Processing failed with status: ${response.status}`);
      }

      if (data.success && data.images && data.images.length > 0) {
        console.log("Processing successful. Images:", data.images);

        // Check if there's an HTML viewer (ends with .html)
        const htmlViewerPath = data.images.find(img => img.endsWith('.html'));
        const gifPath = data.images.find(img => img.endsWith('.gif'));

        if (htmlViewerPath) {
          setHtmlViewer(`${API_URL}${htmlViewerPath}`);
        }

        // Set the primary image (first image or GIF if available)
        if (gifPath) {
          setResultImage(`${API_URL}${gifPath}`);
        } else {
          setResultImage(`${API_URL}${data.images[0]}`);
        }

        // Store all images for 3D rotation effect
        const fullPathImages = data.images.map(img => `${API_URL}${img}`);
        setResultImages(fullPathImages);

        // Reset current image index
        setCurrentImageIndex(0);
      } else {
        throw new Error('No images were generated');
      }
    } catch (error) {
      console.error("Processing error:", error);
      setError(`Error processing files: ${error.message}`);
    } finally {
      setProcessing(false);
    }
  };

  // Function to rotate through images for a pseudo-3D effect
  const startRotation = () => {
    if (resultImages.length <= 1 || isRotating) return;

    setIsRotating(true);
    const interval = setInterval(() => {
      setCurrentImageIndex(prevIndex => {
        const nextIndex = (prevIndex + 1) % resultImages.length;
        setResultImage(resultImages[nextIndex]);
        return nextIndex;
      });
    }, 800); // Change image every 800ms

    // Store the interval ID so we can clear it later
    window.rotationInterval = interval;
  };

  const stopRotation = () => {
    if (window.rotationInterval) {
      clearInterval(window.rotationInterval);
      setIsRotating(false);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      <div className="bg-gray-800 text-white p-4">
        <h1 className="text-2xl font-bold">NELSVIEW: VIEW DICOM IMAGES</h1>
      </div>

      <div className="flex-1 flex flex-col md:flex-row p-4 space-y-4 md:space-y-0 md:space-x-4">
        <div className="w-full md:w-1/4 bg-white rounded-lg shadow p-4">
          <h2 className="text-xl font-semibold mb-4">Controls</h2>

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Select DICOM Folder
            </label>

            {/* Hidden file input that accepts directories */}
            <input
              ref={fileInputRef}
              type="file"
              webkitdirectory="true"
              directory="true"
              style={{ display: 'none' }}
              onChange={handleFolderSelect}
            />

            {/* Custom folder selection button */}
            <button
              onClick={openFolderDialog}
              className="w-full py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Browse Folders
            </button>

            {folderName && (
              <div className="mt-2 text-sm">
                <p className="text-gray-700">
                  <span className="font-medium">Selected Folder:</span> {folderName}
                </p>
                <p className="text-gray-700 mt-1">
                  <span className="font-medium">DICOM Files Found:</span> {fileCount}
                </p>
              </div>
            )}

            <button
              onClick={uploadFiles}
              disabled={loading || files.length === 0}
              className="mt-3 w-full py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-blue-300"
            >
              {loading ? 'Uploading...' : 'Upload Folder'}
            </button>
          </div>

          {uploadId && (
            <>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  View Mode
                </label>
                <div className="flex space-x-2">
                  <button
                    onClick={() => setViewMode('2d')}
                    className={`px-4 py-2 rounded-lg ${
                      viewMode === '2d'
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-200 text-gray-800'
                    }`}
                  >
                    2D Slices
                  </button>
                  <button
                    onClick={() => setViewMode('3d')}
                    className={`px-4 py-2 rounded-lg ${
                      viewMode === '3d'
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-200 text-gray-800'
                    }`}
                  >
                    3D Hologram
                  </button>
                </div>
              </div>

              <button
                onClick={processFiles}
                disabled={processing}
                className="w-full py-2 px-4 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-blue-300"
              >
                {processing ? 'Processing...' : 'Process DICOM Files'}
              </button>

              <div className="mt-4 p-3 bg-blue-50 rounded-lg">
                <p className="text-sm text-blue-800">
                  <strong>Uploaded:</strong> {fileCount} DICOM files from "{folderName}"
                </p>
              </div>

              {viewMode === '3d' && resultImages.length > 1 && (
                <div className="mt-4">
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    3D Controls
                  </label>
                  <div className="flex space-x-2">
                    <button
                      onClick={isRotating ? stopRotation : startRotation}
                      className={`px-4 py-2 rounded-lg ${
                        isRotating
                          ? 'bg-red-500 text-white'
                          : 'bg-green-500 text-white'
                      }`}
                    >
                      {isRotating ? 'Stop Rotation' : 'Start Rotation'}
                    </button>
                  </div>
                </div>
              )}
            </>
          )}
        </div>

        <div className="w-full md:w-3/4 bg-white rounded-lg shadow p-4 flex flex-col">
          <h2 className="text-xl font-semibold mb-4">
            {viewMode === '2d' ? 'DICOM Slice Viewer' : '3D Hologram Viewer'}
          </h2>

          <div className="flex-1 relative">
            {(loading || processing) && (
              <div className="absolute inset-0 flex items-center justify-center bg-gray-200 bg-opacity-75 z-10">
                <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-blue-500"></div>
                <p className="ml-3 font-medium text-gray-700">
                  {loading ? 'Uploading...' : 'Processing...'}
                </p>
              </div>
            )}

            {error && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
                {error}
              </div>
            )}

            {!resultImage && !loading && !processing && !error && (
              <div className="absolute inset-0 flex items-center justify-center bg-gray-100 border-2 border-dashed border-gray-300 rounded-lg">
                <div className="text-center">
                  <p className="text-gray-500">No DICOM files processed</p>
                  <p className="text-gray-400 text-sm mt-2">Select a folder with DICOM files and click "Process DICOM Files"</p>
                </div>
              </div>
            )}

            {htmlViewer && viewMode === '3d' ? (
              <iframe 
                src={htmlViewer}
                title="3D DICOM Viewer"
                className="w-full h-full min-h-96 border-0"
                style={{ minHeight: '500px' }}
              />
            ) : resultImage && (
              <div className="flex flex-col items-center">
                <img 
                  src={resultImage} 
                  alt="DICOM visualization" 
                  className="max-w-full max-h-full object-contain mx-auto"
                  style={{ minHeight: '400px' }}
                />

                {viewMode === '3d' && resultImages.length > 1 && (
                  <div className="mt-4 flex justify-center space-x-2">
                    {resultImages.map((img, index) => (
                      <button
                        key={index}
                        onClick={() => {
                          setResultImage(img);
                          setCurrentImageIndex(index);
                        }}
                        className={`w-3 h-3 rounded-full ${
                          img === resultImage ? 'bg-blue-600' : 'bg-gray-300'
                        }`}
                        title={`View ${index + 1}`}
                      />
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="bg-gray-800 text-white p-2 text-center text-sm">
        NELSVIEW - Â© 2025
      </div>
    </div>
  );
};

export default DicomViewer;
